// const country = "id"
// const urlHeadline = baseUrlTop + "?country=" + country + "&apiKey=" + apiKey;

class Home extends Component{
    componentDidMount = () => {

    };
    render(){
            return (
                <div>
                    <Header {...this.props} doSearch={event => this.props.handleInputChange(event)} doCategory={e => this.props.categoryNews(e)}
                        placeholder="ketik sesuatu"/>
                    <div className="container">
                        <div className="margin-atas-lagi">
                        </div>
                        <div className="margin-atas">
                        </div>
                        <div className= "row align-items-center">
                            <div className="col-md-3">
                                <div className="live-home-img">
                                    <a href={this.props.url} target="_blank" rel="noopener noreferrer"><img src={this.props.img} alt=""/></a>
                                </div>
                            </div>   
                        </div>
                    </div>
                </div>   
            );
        // }
        // else{
        //     return <Redirect to={{ pathname: "/login" }} />;
        // }

    }
}
export default connect(
    "listListCategory, isLoading, listTopArticle, is_login",
    actions
  )(withRouter(Home));
