import React, {Component} from 'react';
import '../assets/css/bootstrap.min.css';
import '../assets/css/main.css';
import Header from '../components/header';
import ListCategory from '../components/list-category';
// import TopArticleData from './top-article-data';
// import axios from "axios";
import { withRouter } from "react-router-dom";
// import { withRouter, Redirect } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store";
// import { actions, store } from "../store";
// import Login from "./login";

const baseUrl = "https://api-todofancy.herokuapp.com/api/movies";
// const apiKey = "448feafa267b4bf4babf102fe746d08b";
// const baseUrlTop = "https://newsapi.org/v2/top-headlines"
// const country = "id"
// const urlHeadline = baseUrlTop + "?country=" + country + "&apiKey=" + apiKey;

class Home extends Component{
    componentDidMount = () => {

    };
    axiosMovies = async() => {
        const category = this.props.match.params.category;
        // const self = this;
        await axios
            .get(`${baseUrl}`)
            .then(function(response){
                store.setState({listMovie: response.data.movies, isLoading: false});
            })
            .catch(function(error){
                store.setState({isLoading: false});
            });
    }
    render(){
            return (
                <div>
                    <Header {...this.props} doSearch={event => this.props.handleInputChange(event)} doCategory={e => this.props.categoryNews(e)}
                        placeholder="ketik sesuatu"/>
                    <div className="container">
                        <ListCategory/>
                    </div>
                </div>   
            );
        // }
        // else{
        //     return <Redirect to={{ pathname: "/login" }} />;
        // }

    }
}
export default connect(
    "listListCategory, isLoading, listTopArticle, is_login",
    actions
  )(withRouter(Home));
