import createStore from "unistore";
import axios from "axios";

const baseUrl = "https://api-todofancy.herokuapp.com/api/movies";
const apiKey = "448feafa267b4bf4babf102fe746d08b";

const initialState = {
    api_key: "",
    email: "",
    password: "",
    full_name: "",
    username: "",
    is_login: false,
    isLoading: true ,
    category: 'romance',
    listMovie: [],
};

export const store = createStore(initialState);

export const actions = store => ({
    handleInputChange: async (state, event) => {
        let value = event.target.value
        await store.setState({ q: value });
        // const self = this;
        if (value.length > 2) {
            try {
                const response = await axios.get(
                    baseUrl + "?q=" + value + "&apiKey=" + apiKey
                );
                store.setState({listListCategory: response.data.articles, isLoading: false});
            } catch (error) {
                console.error(error);
            }
        };
    },
    categoryNews: async (state, event) => {
        // const self = this;
        await axios
            .get(`${baseUrl}?q=${event}&apiKey=448feafa267b4bf4babf102fe746d08b`)
            .then(function(response){
                store.setState({listListCategory: response.data.articles, isLoading: false});
            })
            .catch(function(error){
                store.setState({isLoading: false});
            });
    },
    // ----------------------------------------------------------
    categoryMovies: async (state, event) => {
        // const category = this.props.match.params.category;
        // const self = this;
        await axios
            .get(`${baseUrl}`)
            .then(function(response){
                store.setState({listMovie: response.data.status, isLoading: false});
            })
            .catch(function(error){
                store.setState({isLoading: false});
            });

    }
});
